css:
.art-nouveau-cta-muted-gold-texture-sapphire .container {
  display: flex;
  flex-direction: column;
  background-color: #000080; /* Navy Blue */
  color: #f8f8ff; /* Ghost White */
}
.art-nouveau-cta-muted-gold-texture-sapphire .text-content {
  padding: 40px 20px;
  background-color: #000080; /* Navy Blue */
  text-align: center;
}
.art-nouveau-cta-muted-gold-texture-sapphire h2 {
  font-family: 'Trajan Pro', serif;
  font-size: 2.3em;
  margin-bottom: 15px;
  color: #daa520; /* Goldenrod */
}
.art-nouveau-cta-muted-gold-texture-sapphire p {
  font-family: 'Trajan Pro', serif;
  font-size: 1.1em;
  line-height: 1.6;
  margin-bottom: 30px;
}
.art-nouveau-cta-muted-gold-texture-sapphire .cta-button {
  display: inline-block;
  padding: 14px 28px;
  background-color: #daa520; /* Goldenrod */
  color: #000080; /* Navy Blue */
  text-decoration: none;
  font-family: 'Trajan Pro', serif;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.art-nouveau-cta-muted-gold-texture-sapphire .cta-button:hover {
  background-color: #f8f8ff; /* Ghost White */
  color: #000080; /* Navy Blue */
}
.art-nouveau-cta-muted-gold-texture-sapphire .image-content {
  width: 100%;
  background-color: #000080;
  position: relative;
  overflow: hidden;
}
.art-nouveau-cta-muted-gold-texture-sapphire .image-content img {
  display: block;
  width: 100%;
  height: auto;
  object-fit: cover;
  opacity: 0.7;
  border-top: 5px solid #daa520;
}

@media (min-width: 768px) {
  .art-nouveau-cta-muted-gold-texture-sapphire .container {
    flex-direction: row;
  }
  .art-nouveau-cta-muted-gold-texture-sapphire .text-content {
    flex: 1;
    padding: 60px 40px;
    text-align: left;
  }
  .art-nouveau-cta-muted-gold-texture-sapphire .image-content {
    flex: 1;
  }
  .art-nouveau-cta-muted-gold-texture-sapphire h2 {
    font-size: 2.6em;
  }
  .art-nouveau-cta-muted-gold-texture-sapphire p {
    font-size: 1.15em;
  }
}
