css:
.art-nouveau-cta-organic-lines-gold {
  background-color: #355e3b; /* Forest Green */
  color: #f5f5dc; /* Cream */
  padding: 50px 25px;
  text-align: center;
}
.art-nouveau-cta-organic-lines-gold .container {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.art-nouveau-cta-organic-lines-gold .content {
  margin-bottom: 30px;
}
.art-nouveau-cta-organic-lines-gold h2 {
  font-family: 'Didot', serif;
  font-size: 2.6em;
  margin-bottom: 20px;
  color: #daa520; /* Goldenrod */
  letter-spacing: 1px;
}
.art-nouveau-cta-organic-lines-gold p {
  font-family: 'Didot', serif;
  font-size: 1.15em;
  line-height: 1.7;
  margin-bottom: 35px;
}
.art-nouveau-cta-organic-lines-gold .cta-button {
  display: inline-block;
  padding: 16px 32px;
  background-color: #daa520; /* Goldenrod */
  color: #355e3b; /* Forest Green */
  text-decoration: none;
  font-family: 'Didot', serif;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
  border: 2px solid #daa520;
}
.art-nouveau-cta-organic-lines-gold .cta-button:hover {
  background-color: #f5f5dc; /* Cream */
  color: #355e3b; /* Forest Green */
}
.art-nouveau-cta-organic-lines-gold .ornament-container {
  width: 100%;
  max-width: 350px;
  margin-top: 20px;
}
.art-nouveau-cta-organic-lines-gold .ornament-container img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 8px;
  opacity: 0.8;
}

@media (min-width: 768px) {
  .art-nouveau-cta-organic-lines-gold .container {
    flex-direction: row;
    justify-content: space-between;
  }
  .art-nouveau-cta-organic-lines-gold .content {
    flex: 1;
    text-align: left;
    padding-right: 40px;
    margin-bottom: 0;
  }
  .art-nouveau-cta-organic-lines-gold .ornament-container {
    flex-basis: 40%;
    max-width: none;
    margin-top: 0;
  }
  .art-nouveau-cta-organic-lines-gold h2 {
    font-size: 3em;
  }
  .art-nouveau-cta-organic-lines-gold p {
    font-size: 1.2em;
  }
}
