css:
.art-nouveau-cta-sapphire-cream .container { display: flex; flex-direction: column; align-items: center; padding: 40px 20px; background-color: #0f4c5c; /* Deep Sapphire */ color: #f8f8f8; /* Off-White/Cream */ }
.art-nouveau-cta-sapphire-cream .content { text-align: center; margin-top: 30px; }
.art-nouveau-cta-sapphire-cream h2 { font-family: 'Garamond', serif; font-size: 2.3em; margin-bottom: 15px; color: #e0d6b2; /* Muted Gold/Cream */ }
.art-nouveau-cta-sapphire-cream p { font-family: 'Garamond', serif; font-size: 1.1em; line-height: 1.6; margin-bottom: 30px; }
.art-nouveau-cta-sapphire-cream .cta-button { display: inline-block; padding: 14px 28px; background-color: #e0d6b2; /* Muted Gold/Cream */ color: #0f4c5c; /* Deep Sapphire */ text-decoration: none; font-family: 'Garamond', serif; font-weight: bold; border-radius: 5px; transition: background-color 0.3s ease, color 0.3s ease; }
.art-nouveau-cta-sapphire-cream .cta-button:hover { background-color: #f8f8f8; /* Off-White/Cream */ color: #0f4c5c; /* Deep Sapphire */ }
.art-nouveau-cta-sapphire-cream .image-container { width: 100%; max-width: 400px; }
.art-nouveau-cta-sapphire-cream .image-container img { display: block; width: 100%; height: auto; border-radius: 8px; box-shadow: 0 4px 8px rgba(0, 0, 0, 0.3); }

@media (min-width: 768px) {
  .art-nouveau-cta-sapphire-cream .container { flex-direction: row; text-align: left; padding: 60px 40px; }
  .art-nouveau-cta-sapphire-cream .image-container { flex: 1; margin-right: 40px; }
  .art-nouveau-cta-sapphire-cream .content { flex: 1; margin-top: 0; }
  .art-nouveau-cta-sapphire-cream h2 { font-size: 2.8em; }
  .art-nouveau-cta-sapphire-cream p { font-size: 1.15em; }
}
