css:
.art-nouveau-cta-sapphire-gold-embroidery {
  background-color: #483d8b; /* Dark Slate Blue */
  color: #fff;
  padding: 50px 25px;
  text-align: center;
}
.art-nouveau-cta-sapphire-gold-embroidery .container {
  max-width: 1100px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  align-items: center;
}
.art-nouveau-cta-sapphire-gold-embroidery .content {
  margin-bottom: 30px;
}
.art-nouveau-cta-sapphire-gold-embroidery h2 {
  font-family: 'Palatino Linotype', serif;
  font-size: 2.6em;
  margin-bottom: 15px;
  color: #daa520; /* Goldenrod */
}
.art-nouveau-cta-sapphire-gold-embroidery p {
  font-family: 'Palatino Linotype', serif;
  font-size: 1.15em;
  line-height: 1.6;
  margin-bottom: 30px;
}
.art-nouveau-cta-sapphire-gold-embroidery .cta-button {
  display: inline-block;
  padding: 16px 32px;
  background-color: #daa520; /* Goldenrod */
  color: #483d8b; /* Dark Slate Blue */
  text-decoration: none;
  font-family: 'Palatino Linotype', serif;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.art-nouveau-cta-sapphire-gold-embroidery .cta-button:hover {
  background-color: #fff;
  color: #483d8b; /* Dark Slate Blue */
}
.art-nouveau-cta-sapphire-gold-embroidery .image-container {
  width: 100%;
  max-width: 450px;
  margin-top: 20px;
}
.art-nouveau-cta-sapphire-gold-embroidery .image-container img {
  display: block;
  width: 100%;
  height: auto;
  border-radius: 8px;
  opacity: 0.8;
}

@media (min-width: 768px) {
  .art-nouveau-cta-sapphire-gold-embroidery .container {
    flex-direction: row;
    justify-content: space-between;
    align-items: flex-start;
  }
  .art-nouveau-cta-sapphire-gold-embroidery .content {
    flex: 1;
    padding-right: 40px;
    text-align: left;
  }
  .art-nouveau-cta-sapphire-gold-embroidery .image-container {
    flex-basis: 50%;
    margin-top: 0;
  }
  .art-nouveau-cta-sapphire-gold-embroidery h2 {
    font-size: 3em;
  }
  .art-nouveau-cta-sapphire-gold-embroidery p {
    font-size: 1.2em;
  }
}
