css:
.cta-section-cream-floral {
    position: relative;
    background-color: #f5f5dc;
    color: #3e2723;
    padding: 60px 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    overflow: hidden;
    font-family: 'Lora', serif;
}

.cta-section-cream-floral .cta-background-image {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+cream+floral+pattern+muted&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.3;
    z-index: 1;
}

.cta-section-cream-floral .cta-content {
    position: relative;
    z-index: 2;
    max-width: 650px;
    padding: 40px;
    background: rgba(245, 245, 220, 0.8);
    border-radius: 8px;
}

.cta-section-cream-floral h2 {
    font-size: 2.7rem;
    margin-bottom: 18px;
    color: #8d6e63;
    letter-spacing: 0.5px;
}

.cta-section-cream-floral p {
    font-size: 1.1rem;
    line-height: 1.7;
    margin-bottom: 30px;
    color: #3e2723;
}

.cta-button {
    font-family: 'Lora', serif;
    background-color: #8d6e63;
    color: #f5f5dc;
    border: none;
    padding: 14px 30px;
    font-size: 1.1rem;
    cursor: pointer;
    border-radius: 6px;
    transition: background-color 0.3s ease, transform 0.3s ease;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.cta-button:hover {
    background-color: #a1887f;
    transform: translateY(-3px);
}

@media (max-width: 768px) {
    .cta-section-cream-floral {
        padding: 50px 20px;
    }
    .cta-section-cream-floral .cta-content {
        padding: 35px;
    }
    .cta-section-cream-floral h2 {
        font-size: 2.4rem;
    }
    .cta-section-cream-floral p {
        font-size: 1.05rem;
    }
    .cta-button {
        padding: 12px 28px;
        font-size: 1rem;
    }
}

@media (max-width: 480px) {
    .cta-section-cream-floral {
        padding: 40px 15px;
    }
    .cta-section-cream-floral .cta-content {
        padding: 30px 20px;
    }
    .cta-section-cream-floral h2 {
        font-size: 2.1rem;
    }
    .cta-section-cream-floral p {
        font-size: 1rem;
    }
    .cta-button {
        padding: 10px 25px;
    }
}

/* Placeholder for Pixabay API fetch */
.cta-section-cream-floral .cta-background-image[style*='pixabay.com'] {
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+cream+floral+pattern+muted&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.3;
}
