css:
.cta-section-emerald {
    position: relative;
    background-color: #0a3d2e;
    color: #f0e6d2;
    padding: 60px 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    overflow: hidden;
    text-align: center;
}

.cta-section-emerald .cta-content {
    position: relative;
    z-index: 2;
    max-width: 700px;
}

.cta-section-emerald h2 {
    font-family: 'Playfair Display', serif;
    font-size: 2.8rem;
    margin-bottom: 20px;
    color: #e0c28e;
    letter-spacing: 1px;
}

.cta-section-emerald p {
    font-family: 'Lato', sans-serif;
    font-size: 1.1rem;
    line-height: 1.6;
    margin-bottom: 30px;
    color: #f0e6d2;
}

.cta-button {
    font-family: 'Playfair Display', serif;
    background-color: #e0c28e;
    color: #0a3d2e;
    border: none;
    padding: 15px 30px;
    font-size: 1.1rem;
    cursor: pointer;
    border-radius: 5px;
    transition: background-color 0.3s ease, transform 0.3s ease;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.cta-button:hover {
    background-color: #f0e6d2;
    color: #0a3d2e;
    transform: translateY(-3px);
}

.cta-background-element {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+floral+pattern+emerald+gold&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.2;
    z-index: 1;
}

@media (max-width: 768px) {
    .cta-section-emerald {
        padding: 40px 15px;
    }
    .cta-section-emerald h2 {
        font-size: 2.2rem;
    }
    .cta-section-emerald p {
        font-size: 1rem;
    }
    .cta-button {
        font-size: 1rem;
        padding: 12px 25px;
    }
}

@media (max-width: 480px) {
    .cta-section-emerald h2 {
        font-size: 2rem;
    }
    .cta-section-emerald p {
        font-size: 0.95rem;
    }
    .cta-button {
        padding: 10px 20px;
    }
}

/* Placeholder for Pixabay API fetch */
.cta-section-emerald img[src^='https://pixabay.com/api/'] {
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+floral+pattern+emerald+gold&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.2;
}
