css:
.cta-section-gold-leaf {
    position: relative;
    background-color: #261a0a;
    color: #e0c28e;
    padding: 90px 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    overflow: hidden;
    font-family: 'Cinzel Decorative', cursive;
}

.cta-section-gold-leaf .cta-background-art {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+gold+leaf+pattern+ornate&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.2;
    z-index: 1;
}

.cta-section-gold-leaf .cta-content {
    position: relative;
    z-index: 2;
    max-width: 700px;
    padding: 50px;
    background: rgba(38, 26, 10, 0.7);
    border-radius: 15px;
    border: 3px solid #e0c28e;
}

.cta-section-gold-leaf h2 {
    font-size: 3rem;
    margin-bottom: 20px;
    color: #ffffff;
    text-shadow: 2px 2px 5px rgba(0,0,0,0.4);
}

.cta-section-gold-leaf p {
    font-size: 1.2rem;
    line-height: 1.7;
    margin-bottom: 40px;
    font-family: 'Lato', sans-serif;
    color: rgba(224, 194, 142, 0.9);
}

.cta-button {
    font-family: 'Cinzel Decorative', cursive;
    background-color: #e0c28e;
    color: #261a0a;
    border: none;
    padding: 18px 40px;
    font-size: 1.2rem;
    cursor: pointer;
    border-radius: 8px;
    transition: background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease;
    text-transform: uppercase;
    letter-spacing: 1.5px;
    box-shadow: 0 4px 10px rgba(224, 194, 142, 0.3);
}

.cta-button:hover {
    background-color: #f0e6d2;
    color: #261a0a;
    transform: translateY(-4px);
    box-shadow: 0 6px 15px rgba(240, 230, 210, 0.4);
}

@media (max-width: 768px) {
    .cta-section-gold-leaf {
        padding: 70px 20px;
    }
    .cta-section-gold-leaf .cta-content {
        padding: 40px;
    }
    .cta-section-gold-leaf h2 {
        font-size: 2.6rem;
    }
    .cta-section-gold-leaf p {
        font-size: 1.1rem;
    }
    .cta-button {
        padding: 15px 35px;
        font-size: 1.1rem;
    }
}

@media (max-width: 480px) {
    .cta-section-gold-leaf {
        padding: 60px 15px;
    }
    .cta-section-gold-leaf .cta-content {
        padding: 30px;
    }
    .cta-section-gold-leaf h2 {
        font-size: 2.3rem;
    }
    .cta-section-gold-leaf p {
        font-size: 1rem;
    }
    .cta-button {
        padding: 12px 30px;
        font-size: 1rem;
    }
}

/* Placeholder for Pixabay API fetch */
.cta-section-gold-leaf .cta-background-art[style*='pixabay.com'] {
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+gold+leaf+pattern+ornate&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.2;
}
