css:
.cta-section-jewel-texture {
    position: relative;
    background-color: #311b92; /* Deep Amethyst */
    color: #ffffff;
    padding: 70px 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    overflow: hidden;
    font-family: 'Dancing Script', cursive;
}

.cta-section-jewel-texture .cta-texture-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+jewel+tone+texture+overlay+gold&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.2;
    z-index: 1;
    box-shadow: inset 0 0 50px rgba(0,0,0,0.5);
}

.cta-section-jewel-texture .cta-content {
    position: relative;
    z-index: 2;
    max-width: 650px;
    padding: 50px;
    background: rgba(49, 27, 146, 0.7);
    border-radius: 10px;
}

.cta-section-jewel-texture h2 {
    font-size: 2.7rem;
    margin-bottom: 18px;
    color: #fbc02d; /* Gold */
    letter-spacing: 0.8px;
    text-shadow: 1px 1px 3px rgba(0,0,0,0.3);
}

.cta-section-jewel-texture p {
    font-size: 1.15rem;
    line-height: 1.7;
    margin-bottom: 35px;
    color: rgba(255, 255, 255, 0.9);
}

.cta-button {
    font-family: 'Dancing Script', cursive;
    background-color: #fbc02d;
    color: #311b92;
    border: none;
    padding: 16px 38px;
    font-size: 1.2rem;
    cursor: pointer;
    border-radius: 30px;
    transition: background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease;
    font-weight: bold;
    letter-spacing: 1.5px;
    box-shadow: 0 5px 12px rgba(251, 192, 45, 0.4);
}

.cta-button:hover {
    background-color: #f9a825;
    color: #311b92;
    transform: translateY(-4px);
    box-shadow: 0 7px 18px rgba(249, 168, 37, 0.5);
}

@media (max-width: 768px) {
    .cta-section-jewel-texture {
        padding: 60px 15px;
    }
    .cta-section-jewel-texture .cta-content {
        padding: 40px;
    }
    .cta-section-jewel-texture h2 {
        font-size: 2.4rem;
    }
    .cta-section-jewel-texture p {
        font-size: 1.1rem;
    }
    .cta-button {
        padding: 14px 32px;
        font-size: 1.1rem;
    }
}

@media (max-width: 480px) {
    .cta-section-jewel-texture {
        padding: 50px 15px;
    }
    .cta-section-jewel-texture .cta-content {
        padding: 30px;
    }
    .cta-section-jewel-texture h2 {
        font-size: 2.1rem;
    }
    .cta-section-jewel-texture p {
        font-size: 1rem;
    }
    .cta-button {
        padding: 12px 30px;
    }
}

/* Placeholder for Pixabay API fetch */
.cta-section-jewel-texture .cta-texture-overlay[style*='pixabay.com'] {
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+jewel+tone+texture+overlay+gold&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.2;
    box-shadow: inset 0 0 50px rgba(0,0,0,0.5);
}
