css:
.cta-section-jewel-abstract {
    position: relative;
    background-color: #0f0f0f;
    color: #ffffff;
    padding: 75px 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    overflow: hidden;
    font-family: 'Cormorant Garamond', serif;
}

.cta-section-jewel-abstract .cta-abstract-overlay {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+abstract+jewel+tones+gold+overlay&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.18;
    z-index: 1;
    mix-blend-mode: overlay;
}

.cta-section-jewel-abstract .cta-content {
    position: relative;
    z-index: 2;
    max-width: 680px;
    padding: 50px;
    background: rgba(15, 15, 15, 0.8);
    border-radius: 10px;
    border: 1px solid rgba(218, 165, 32, 0.5);
}

.cta-section-jewel-abstract h2 {
    font-size: 2.8rem;
    margin-bottom: 20px;
    color: #ba68c8; /* Amethyst */
    letter-spacing: 1px;
    text-shadow: 1px 1px 3px rgba(0,0,0,0.3);
}

.cta-section-jewel-abstract p {
    font-size: 1.15rem;
    line-height: 1.7;
    margin-bottom: 35px;
    color: rgba(255, 255, 255, 0.9);
}

.cta-button {
    font-family: 'Cormorant Garamond', serif;
    background-color: #4fc3f7; /* Sapphire accent */
    color: #0f0f0f;
    border: none;
    padding: 16px 35px;
    font-size: 1.15rem;
    cursor: pointer;
    border-radius: 30px;
    transition: background-color 0.3s ease, transform 0.3s ease, box-shadow 0.3s ease;
    font-weight: bold;
    letter-spacing: 1.2px;
    box-shadow: 0 4px 10px rgba(79, 195, 247, 0.3);
}

.cta-button:hover {
    background-color: #81d4fa;
    color: #0f0f0f;
    transform: translateY(-3px);
    box-shadow: 0 6px 15px rgba(129, 212, 250, 0.4);
}

@media (max-width: 768px) {
    .cta-section-jewel-abstract {
        padding: 60px 20px;
    }
    .cta-section-jewel-abstract .cta-content {
        padding: 40px;
    }
    .cta-section-jewel-abstract h2 {
        font-size: 2.4rem;
    }
    .cta-section-jewel-abstract p {
        font-size: 1.1rem;
    }
    .cta-button {
        padding: 14px 30px;
        font-size: 1.1rem;
    }
}

@media (max-width: 480px) {
    .cta-section-jewel-abstract {
        padding: 50px 15px;
    }
    .cta-section-jewel-abstract .cta-content {
        padding: 30px;
    }
    .cta-section-jewel-abstract h2 {
        font-size: 2.1rem;
    }
    .cta-section-jewel-abstract p {
        font-size: 1rem;
    }
    .cta-button {
        padding: 12px 28px;
        font-size: 1rem;
    }
}

/* Placeholder for Pixabay API fetch */
.cta-section-jewel-abstract .cta-abstract-overlay[style*='pixabay.com'] {
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+abstract+jewel+tones+gold+overlay&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.18;
    mix-blend-mode: overlay;
}
