css:
.cta-section-muted-gold-floral {
    position: relative;
    background-color: #f8f8f8; /* Off-white */
    color: #333333;
    padding: 70px 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    overflow: hidden;
    font-family: 'EB Garamond', serif;
}

.cta-section-muted-gold-floral .cta-pattern-background {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+muted+gold+floral+pattern+background&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.4;
    z-index: 1;
}

.cta-section-muted-gold-floral .cta-content {
    position: relative;
    z-index: 2;
    max-width: 680px;
    padding: 50px;
    background: rgba(248, 248, 248, 0.9);
    border-radius: 10px;
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
}

.cta-section-muted-gold-floral h2 {
    font-size: 2.7rem;
    margin-bottom: 18px;
    color: #bcaaa4; /* Muted Gold/Brown */
    letter-spacing: 0.8px;
}

.cta-section-muted-gold-floral p {
    font-size: 1.15rem;
    line-height: 1.7;
    margin-bottom: 35px;
    color: #333333;
}

.cta-button {
    font-family: 'EB Garamond', serif;
    background-color: #bcaaa4;
    color: #212121;
    border: none;
    padding: 16px 38px;
    font-size: 1.15rem;
    cursor: pointer;
    border-radius: 5px;
    transition: background-color 0.3s ease, transform 0.3s ease;
    font-weight: bold;
    letter-spacing: 1.2px;
}

.cta-button:hover {
    background-color: #a1887f;
    transform: translateY(-4px);
}

@media (max-width: 768px) {
    .cta-section-muted-gold-floral {
        padding: 60px 15px;
    }
    .cta-section-muted-gold-floral .cta-content {
        padding: 40px;
    }
    .cta-section-muted-gold-floral h2 {
        font-size: 2.4rem;
    }
    .cta-section-muted-gold-floral p {
        font-size: 1.1rem;
    }
    .cta-button {
        padding: 14px 32px;
        font-size: 1.1rem;
    }
}

@media (max-width: 480px) {
    .cta-section-muted-gold-floral {
        padding: 50px 15px;
    }
    .cta-section-muted-gold-floral .cta-content {
        padding: 30px;
    }
    .cta-section-muted-gold-floral h2 {
        font-size: 2.1rem;
    }
    .cta-section-muted-gold-floral p {
        font-size: 1rem;
    }
    .cta-button {
        padding: 12px 30px;
    }
}

/* Placeholder for Pixabay API fetch */
.cta-section-muted-gold-floral .cta-pattern-background[style*='pixabay.com'] {
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+muted+gold+floral+pattern+background&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.4;
}
