css:
.cta-section-muted-gold-frame {
    position: relative;
    background-color: #e8d5b5;
    padding: 60px 20px;
    display: flex;
    justify-content: center;
    align-items: center;
    text-align: center;
    overflow: hidden;
    font-family: 'Merriweather', serif;
}

.cta-section-muted-gold-frame .cta-frame {
    position: relative;
    width: 100%;
    max-width: 750px;
    padding: 50px;
    background-color: #3e2723;
    border: 5px solid #d4af37;
    border-radius: 15px;
    box-shadow: 0 10px 25px rgba(0,0,0,0.4);
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+floral+border+muted+gold+texture&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.15;
    z-index: 1;
}

.cta-section-muted-gold-frame .cta-content {
    position: relative;
    z-index: 2;
    color: #ffffff;
    padding: 30px;
    background: rgba(62, 39, 35, 0.8);
    border-radius: 8px;
}

.cta-section-muted-gold-frame h2 {
    font-size: 2.5rem;
    margin-bottom: 15px;
    color: #d4af37;
    letter-spacing: 0.8px;
    font-family: 'Playfair Display', serif;
}

.cta-section-muted-gold-frame p {
    font-size: 1.1rem;
    line-height: 1.7;
    margin-bottom: 30px;
    color: rgba(255, 255, 255, 0.9);
}

.cta-button {
    font-family: 'Merriweather', serif;
    background-color: #d4af37;
    color: #3e2723;
    border: none;
    padding: 14px 32px;
    font-size: 1.1rem;
    cursor: pointer;
    border-radius: 5px;
    transition: background-color 0.3s ease, transform 0.3s ease;
    font-weight: bold;
    text-transform: uppercase;
    letter-spacing: 1px;
}

.cta-button:hover {
    background-color: #e0c28e;
    transform: translateY(-3px);
}

@media (max-width: 768px) {
    .cta-section-muted-gold-frame {
        padding: 50px 15px;
    }
    .cta-section-muted-gold-frame .cta-frame {
        padding: 40px;
    }
    .cta-section-muted-gold-frame h2 {
        font-size: 2.2rem;
    }
    .cta-section-muted-gold-frame p {
        font-size: 1rem;
    }
    .cta-button {
        padding: 12px 28px;
        font-size: 1rem;
    }
}

@media (max-width: 480px) {
    .cta-section-muted-gold-frame {
        padding: 40px 15px;
    }
    .cta-section-muted-gold-frame .cta-frame {
        padding: 30px;
    }
    .cta-section-muted-gold-frame h2 {
        font-size: 1.9rem;
    }
    .cta-section-muted-gold-frame p {
        font-size: 0.95rem;
    }
    .cta-button {
        padding: 10px 25px;
    }
}

/* Placeholder for Pixabay API fetch */
.cta-section-muted-gold-frame .cta-frame[style*='pixabay.com'] {
    background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=art+nouveau+floral+border+muted+gold+texture&image_type=photo');
    background-size: cover;
    background-position: center;
    opacity: 0.15;
}
