css:
.cta-animated-button {
    padding: 70px 20px;
    background-color: #f4f7f6;
    text-align: center;
}
.cta-animated-button .container {
    max-width: 700px;
    margin: 0 auto;
}
.cta-animated-button h2 {
    font-size: 2.8em;
    color: #34495e;
    margin-bottom: 15px;
    font-family: 'Lato', sans-serif;
}
.cta-animated-button p {
    font-size: 1.15em;
    color: #555;
    line-height: 1.7;
    margin-bottom: 40px;
    font-family: 'Lato', sans-serif;
}
.cta-button-animated {
    display: inline-block;
    padding: 18px 45px;
    background-color: #1abc9c;
    color: white;
    text-decoration: none;
    font-size: 1.2em;
    font-weight: bold;
    border-radius: 8px;
    position: relative;
    overflow: hidden;
    transition: all 0.4s ease-out;
    font-family: 'Lato', sans-serif;
}
.cta-button-animated::before {
    content: '';
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(255, 255, 255, 0.2);
    transform: translateX(-100%);
    transition: transform 0.4s ease-out;
}
.cta-button-animated:hover {
    background-color: #16a085;
    transform: translateY(-3px);
    box-shadow: 0 6px 12px rgba(0, 0, 0, 0.2);
}
.cta-button-animated:hover::before {
    transform: translateX(100%);
}
@media (max-width: 768px) {
    .cta-animated-button h2 {
        font-size: 2.4em;
    }
    .cta-button-animated {
        font-size: 1.1em;
        padding: 15px 40px;
    }
}
@media (max-width: 480px) {
    .cta-animated-button {
        padding: 50px 15px;
    }
    .cta-button-animated {
        width: 100%;
        box-sizing: border-box;
    }
}
