css:
.cta-atomic-retro-orange {
  background-color: #f5a623; /* Vibrant Orange */
  color: #ffffff;
  padding: 60px 20px;
  text-align: center;
}
.cta-atomic-retro-orange .container {
  max-width: 700px;
  margin: 0 auto;
}
.cta-atomic-retro-orange h2 {
  font-size: 2.5em;
  margin-bottom: 15px;
  font-family: 'Futura PT', sans-serif;
  letter-spacing: 1px;
  text-shadow: 1px 1px 3px rgba(0, 0, 0, 0.2);
}
.cta-atomic-retro-orange p {
  font-size: 1.1em;
  line-height: 1.6;
  margin-bottom: 30px;
  font-family: 'Avenir Next', sans-serif;
}
.cta-atomic-retro-orange .cta-button {
  display: inline-block;
  padding: 15px 35px;
  background-color: #2c3e50; /* Darker Navy */
  color: #ffffff;
  text-decoration: none;
  font-weight: bold;
  border-radius: 30px; /* Rounded */
  transition: background-color 0.3s ease, transform 0.3s ease;
  font-family: 'Futura PT', sans-serif;
  box-shadow: 0 4px 10px rgba(0, 0, 0, 0.2);
}
.cta-atomic-retro-orange .cta-button:hover {
  background-color: #34495e;
  transform: translateY(-3px);
}

@media (min-width: 768px) {
  .cta-atomic-retro-orange {
    padding: 80px 40px;
  }
  .cta-atomic-retro-orange h2 {
    font-size: 3em;
  }
  .cta-atomic-retro-orange p {
    font-size: 1.2em;
  }
}
