css:
.cta-bamboo-grove { position: relative; background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=bamboo+forest+sunlight&image_type=photo'); background-size: cover; background-position: center; padding: 80px 20px; text-align: center; color: #1b5e20; /* Dark green */ font-family: 'Quicksand', sans-serif; }
.cta-bamboo-grove::before { content: ''; position: absolute; top: 0; left: 0; width: 100%; height: 100%; background-color: rgba(139, 195, 74, 0.3); /* Soft green tint */ }
.cta-bamboo-grove .container { position: relative; z-index: 2; }
.cta-bamboo-grove h2 { font-size: 2.8rem; margin-bottom: 15px; color: #2e7d32; /* Forest green */ }
.cta-bamboo-grove p { font-size: 1.1rem; line-height: 1.6; margin-bottom: 30px; color: #388e3c; /* Lighter forest green */ }
.cta-bamboo-grove .cta-button { display: inline-block; padding: 14px 30px; background-color: #66bb6a; /* Medium green */ color: #1b5e20; text-decoration: none; border-radius: 50px; /* Rounded */ font-weight: bold; font-size: 1.1rem; transition: background-color 0.3s ease, transform 0.3s ease; }
.cta-bamboo-grove .cta-button:hover { background-color: #4caf50; transform: translateY(-2px); }
.cta-bamboo-grove .bamboo-overlay { position: absolute; top: 0; left: 0; width: 100%; height: 100%; background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=subtle+bamboo+pattern+overlay&image_type=photo'); background-size: cover; background-position: center; opacity: 0.2; z-index: 1; }

@media (min-width: 768px) {
  .cta-bamboo-grove { padding: 100px 40px; }
  .cta-bamboo-grove h2 { font-size: 3.5rem; }
  .cta-bamboo-grove p { font-size: 1.3rem; }
  .cta-bamboo-grove .cta-button { font-size: 1.2rem; }
}
