css:
.cta-bold-orange-minimal {
  padding: 40px 20px;
  background-color: #fafafa;
  text-align: center;
}
.cta-bold-orange-minimal .container {
  max-width: 650px;
  margin: 0 auto;
}
.cta-bold-orange-minimal h2 {
  font-size: 30px;
  margin-bottom: 15px;
  color: #d35400;
  font-family: 'Helvetica Neue', sans-serif;
}
.cta-bold-orange-minimal p {
  font-size: 16px;
  margin-bottom: 30px;
  color: #444;
  line-height: 1.6;
  font-family: 'Helvetica Neue', sans-serif;
}
.cta-bold-orange-minimal .btn {
  display: inline-block;
  padding: 12px 35px;
  background-color: #e67e22;
  color: #fff;
  text-decoration: none;
  font-size: 16px;
  font-weight: bold;
  border-radius: 50px;
  transition: background-color 0.3s ease;
  font-family: 'Helvetica Neue', sans-serif;
}
.cta-bold-orange-minimal .btn:hover {
  background-color: #c0392b;
}

@media (min-width: 768px) {
  .cta-bold-orange-minimal {
    padding: 60px 20px;
  }
  .cta-bold-orange-minimal h2 {
    font-size: 40px;
  }
  .cta-bold-orange-minimal p {
    font-size: 18px;
  }
  .cta-bold-orange-minimal .btn {
    font-size: 18px;
  }
}
