css:
.cta-earthy-tones-image-quote {
  display: flex;
  flex-direction: column;
  font-family: 'Merriweather', serif;
  background-color: #f5f5dc; /* Beige */
}
.cta-earthy-tones-image-quote .image-container {
  width: 100%;
  height: 250px;
  overflow: hidden;
}
.cta-earthy-tones-image-quote .image-container img {
  width: 100%;
  height: 100%;
  object-fit: cover;
  opacity: 0.8;
}
.cta-earthy-tones-image-quote .content-container {
  padding: 40px 20px;
  text-align: center;
  color: #5a463a; /* Dark Brown */
  flex: 1;
}
.cta-earthy-tones-image-quote h3 {
  font-size: 26px;
  margin-bottom: 15px;
  color: #8b4513; /* Saddle Brown */
}
.cta-earthy-tones-image-quote p {
  font-size: 16px;
  margin-bottom: 30px;
  line-height: 1.7;
}
.cta-earthy-tones-image-quote .btn {
  display: inline-block;
  padding: 12px 30px;
  background-color: #8b4513;
  color: #fff;
  text-decoration: none;
  font-size: 16px;
  border-radius: 6px;
  transition: background-color 0.3s ease, transform 0.3s ease;
}
.cta-earthy-tones-image-quote .btn:hover {
  background-color: #5a463a;
  transform: translateY(-2px);
}

@media (min-width: 768px) {
  .cta-earthy-tones-image-quote {
    flex-direction: row;
  }
  .cta-earthy-tones-image-quote .image-container {
    width: 50%;
    height: auto;
  }
  .cta-earthy-tones-image-quote .content-container {
    width: 50%;
    padding: 50px 40px;
    text-align: left;
  }
  .cta-earthy-tones-image-quote h3 {
    font-size: 32px;
  }
  .cta-earthy-tones-image-quote p {
    font-size: 17px;
  }
  .cta-earthy-tones-image-quote .btn {
    font-size: 17px;
  }
}
