css:
.cta-elegant-gold-dark {
  padding: 50px 20px;
  background-color: #1a1a1a;
  color: #fff;
  text-align: center;
}
.cta-elegant-gold-dark .container {
  max-width: 700px;
  margin: 0 auto;
}
.cta-elegant-gold-dark h2 {
  font-size: 32px;
  margin-bottom: 15px;
  color: #ffd700;
  font-family: 'Playfair Display', serif;
}
.cta-elegant-gold-dark p {
  font-size: 17px;
  margin-bottom: 30px;
  color: #ccc;
  line-height: 1.7;
  font-family: 'Lato', sans-serif;
}
.cta-elegant-gold-dark .btn {
  display: inline-block;
  padding: 14px 35px;
  background-color: transparent;
  color: #ffd700;
  text-decoration: none;
  font-size: 17px;
  font-weight: bold;
  border: 2px solid #ffd700;
  border-radius: 30px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-elegant-gold-dark .btn:hover {
  background-color: #ffd700;
  color: #1a1a1a;
}

@media (min-width: 768px) {
  .cta-elegant-gold-dark {
    padding: 70px 20px;
  }
  .cta-elegant-gold-dark h2 {
    font-size: 42px;
  }
  .cta-elegant-gold-dark p {
    font-size: 19px;
  }
  .cta-elegant-gold-dark .btn {
    font-size: 19px;
  }
}
