css:
.cta-foliage-frame { position: relative; background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=lush+green+foliage+background&image_type=photo'); background-size: cover; background-position: center; padding: 80px 20px; color: white; text-align: center; font-family: 'Nunito Sans', sans-serif; }
.cta-foliage-frame::before { content: ''; position: absolute; top: 0; left: 0; width: 100%; height: 100%; background-color: rgba(46, 125, 50, 0.7); /* Deep green overlay */ }
.cta-foliage-frame .container { position: relative; z-index: 2; }
.cta-foliage-frame h2 { font-size: 2.8rem; margin-bottom: 15px; color: #e0f2f1; }
.cta-foliage-frame p { font-size: 1.1rem; line-height: 1.6; margin-bottom: 30px; color: #b2dfdb; }
.cta-foliage-frame .cta-button { display: inline-block; padding: 14px 30px; background-color: #a5d6a7; color: #1b5e20; text-decoration: none; border-radius: 8px; font-weight: bold; font-size: 1.1rem; transition: background-color 0.3s ease, transform 0.3s ease; }
.cta-foliage-frame .cta-button:hover { background-color: #81c784; transform: translateY(-2px); }
.cta-foliage-frame .foliage-overlay { position: absolute; top: 0; left: 0; width: 100%; height: 100%; background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=subtle+leaf+pattern+overlay&image_type=photo'); background-size: cover; background-position: center; opacity: 0.3; z-index: 1; }

@media (min-width: 768px) {
  .cta-foliage-frame { padding: 100px 40px; }
  .cta-foliage-frame h2 { font-size: 3.5rem; }
  .cta-foliage-frame p { font-size: 1.3rem; }
  .cta-foliage-frame .cta-button { font-size: 1.2rem; }
}
