css:
.cta-forest-path-entry { background-image: url('https://pixabay.com/api/?key=52494880-f6ccd6f4c90db08ed73d4f14d&q=enchanting+forest+path+with+sunlight&image_type=photo'); background-size: cover; background-position: center; padding: 80px 20px; text-align: center; color: #fff; font-family: 'Merriweather Sans', sans-serif; position: relative; }
.cta-forest-path-entry::before { content: ''; position: absolute; top: 0; left: 0; width: 100%; height: 100%; background-color: rgba(0, 50, 0, 0.5); /* Deep forest green overlay */ }
.cta-forest-path-entry .container { max-width: 800px; margin: 0 auto; position: relative; z-index: 1; }
.cta-forest-path-entry h2 { font-size: 2.8rem; margin-bottom: 15px; color: #dcedc8; /* Light green accent */ }
.cta-forest-path-entry p { font-size: 1.15rem; line-height: 1.7; margin-bottom: 35px; color: #aed581; /* Lighter accent green */ }
.cta-forest-path-entry .cta-button { display: inline-block; padding: 14px 30px; background-color: #556b2f; /* Earthy brown-green */ color: #fff; text-decoration: none; border-radius: 5px; font-weight: bold; font-size: 1.1rem; transition: background-color 0.3s ease, transform 0.3s ease; }
.cta-forest-path-entry .cta-button:hover { background-color: #3e4a2b; transform: translateY(-2px); }

@media (min-width: 768px) {
  .cta-forest-path-entry { padding: 100px 40px; }
  .cta-forest-path-entry h2 { font-size: 3.5rem; }
  .cta-forest-path-entry p { font-size: 1.3rem; }
  .cta-forest-path-entry .cta-button { font-size: 1.2rem; }
}
