css:
.cta-gradient-banner .container {
  width: 90%;
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 1rem;
}
.cta-gradient-banner {
    padding: 5rem 2rem;
    background: linear-gradient(135deg, var(--color-primary, #667eea) 0%, var(--color-secondary, #764ba2) 100%);
    color: var(--color-light, #FFFFFF);
    text-align: center;
    border-radius: 16px;
    margin: 2rem;
}
.cta-gradient-banner h2 { font-size: 2.5rem; margin-bottom: 1rem; }
.cta-gradient-banner p { max-width: 600px; margin: 0 auto 2rem; opacity: 0.9; }
.btn-cta {
    display: inline-block;
    padding: 1rem 2.5rem;
    background-color: var(--color-light, #FFFFFF);
    color: var(--color-dark, #111827);
    border-radius: 50px;
    font-weight: 700;
    text-decoration: none;
    transition: transform 0.3s, box-shadow 0.3s;
}
.btn-cta:hover { transform: translateY(-3px); box-shadow: 0 10px 20px rgba(0,0,0,0.2); }
