css:
.cta-minimal-block-red {
  padding: 40px 20px;
  background-color: #f4f4f4;
  text-align: center;
  font-family: 'Arial Narrow', sans-serif;
}
.cta-minimal-block-red .container {
  max-width: 550px;
  margin: 0 auto;
  background-color: #e74c3c;
  color: #fff;
  padding: 40px 20px;
  border-radius: 8px;
  box-shadow: 0 4px 15px rgba(0,0,0,0.1);
}
.cta-minimal-block-red h2 {
  font-size: 30px;
  margin-bottom: 10px;
}
.cta-minimal-block-red p {
  font-size: 16px;
  margin-bottom: 25px;
  line-height: 1.5;
}
.cta-minimal-block-red .btn {
  display: inline-block;
  padding: 10px 25px;
  background-color: #fff;
  color: #e74c3c;
  text-decoration: none;
  font-size: 16px;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-minimal-block-red .btn:hover {
  background-color: #c0392b;
  color: #fff;
}

@media (min-width: 768px) {
  .cta-minimal-block-red {
    padding: 60px 20px;
  }
  .cta-minimal-block-red .container {
    padding: 50px 30px;
  }
  .cta-minimal-block-red h2 {
    font-size: 40px;
  }
  .cta-minimal-block-red p {
    font-size: 18px;
  }
  .cta-minimal-block-red .btn {
    font-size: 18px;
  }
}
