css:
.cta-section-07 {
  background-color: #fefefe; /* Almost white */
  padding: 60px 20px;
  text-align: center;
  border-top: 1px solid #eee;
  border-bottom: 1px solid #eee;
}
.cta-container-07 {
  max-width: 700px;
  margin: 0 auto;
  background-color: #fffaf0; /* Pale yellow */
  padding: 50px 30px;
  border-radius: 8px;
  box-shadow: 0 8px 24px rgba(0, 0, 0, 0.04);
  position: relative;
  overflow: hidden;
}
.cta-container-07::before {
  content: '';
  position: absolute;
  top: -20px;
  left: -20px;
  width: 100px;
  height: 100px;
  background-color: rgba(255, 228, 181, 0.5); /* Light peach/yellow accent */
  border-radius: 50%;
  filter: blur(30px);
  z-index: 0;
}
.cta-container-07::after {
  content: '';
  position: absolute;
  bottom: -20px;
  right: -20px;
  width: 80px;
  height: 80px;
  background-color: rgba(173, 216, 230, 0.4); /* Light blue accent */
  border-radius: 50%;
  filter: blur(25px);
  z-index: 0;
}
.cta-container-07 h2 {
  font-family: 'Poppins', sans-serif;
  font-size: 2.5em;
  color: #6b5a4a; /* Deep brown */
  margin-bottom: 15px;
  position: relative;
  z-index: 1;
}
.cta-container-07 p {
  font-family: 'Roboto', sans-serif;
  font-size: 1.1em;
  color: #8a7a6a;
  line-height: 1.7;
  margin-bottom: 30px;
  position: relative;
  z-index: 1;
}
.cta-form-container-07 {
  display: flex;
  justify-content: center;
  gap: 10px;
  flex-wrap: wrap;
  position: relative;
  z-index: 1;
}
.cta-form-container-07 input[type="email"] {
  font-family: 'Roboto', sans-serif;
  padding: 15px 20px;
  border: 1px solid #e0d8c4; /* Light wood tone border */
  border-radius: 5px;
  font-size: 1em;
  flex-grow: 1;
  min-width: 220px;
  background-color: #fff;
}
.cta-form-container-07 input[type="email"]::placeholder {
  color: #c0b5a0;
}
.cta-form-container-07 button {
  font-family: 'Poppins', sans-serif;
  background-color: #a08a70; /* Mid wood tone */
  color: #ffffff;
  padding: 15px 30px;
  border: none;
  border-radius: 5px;
  font-size: 1.05em;
  font-weight: bold;
  cursor: pointer;
  transition: background-color 0.3s ease;
}
.cta-form-container-07 button:hover {
  background-color: #806a50;
}
@media (min-width: 768px) {
  .cta-section-07 {
    padding: 90px 40px;
  }
  .cta-container-07 {
    padding: 60px 50px;
  }
  .cta-container-07 h2 {
    font-size: 3.2em;
  }
  .cta-container-07 p {
    font-size: 1.2em;
  }
}
