css:
.cta-modern-business-hero {
  display: flex;
  flex-direction: column;
  align-items: center;
  padding: 40px 20px;
  background-color: #f8f9fa;
  color: #333;
}
.cta-modern-business-hero .content-wrapper {
  text-align: center;
  margin-bottom: 30px;
}
.cta-modern-business-hero h1 {
  font-size: 2.2em;
  margin-bottom: 15px;
  color: #007bff;
}
.cta-modern-business-hero p {
  font-size: 1.1em;
  line-height: 1.5;
  margin-bottom: 30px;
}
.cta-modern-business-hero .cta-actions {
  display: flex;
  flex-direction: column;
  gap: 15px;
}
.cta-modern-business-hero .btn {
  display: inline-block;
  padding: 12px 25px;
  text-decoration: none;
  font-weight: bold;
  border-radius: 5px;
  text-align: center;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-modern-business-hero .btn-primary {
  background-color: #007bff;
  color: #ffffff;
  border: 2px solid #007bff;
}
.cta-modern-business-hero .btn-primary:hover {
  background-color: #0056b3;
  border-color: #0056b3;
}
.cta-modern-business-hero .btn-secondary {
  background-color: transparent;
  color: #007bff;
  border: 2px solid #007bff;
}
.cta-modern-business-hero .btn-secondary:hover {
  background-color: #007bff;
  color: #ffffff;
}
.cta-modern-business-hero .image-container img {
  max-width: 100%;
  height: auto;
  display: block;
  margin: 0 auto;
  border-radius: 8px;
}
@media (min-width: 768px) {
  .cta-modern-business-hero {
    flex-direction: row;
    text-align: left;
    padding: 60px 40px;
    justify-content: space-between;
    align-items: center;
  }
  .cta-modern-business-hero .content-wrapper {
    flex: 1;
    max-width: 50%;
    margin-bottom: 0;
    padding-right: 40px;
  }
  .cta-modern-business-hero h1 {
    font-size: 2.8em;
  }
  .cta-modern-business-hero p {
    font-size: 1.2em;
  }
  .cta-modern-business-hero .cta-actions {
    flex-direction: row;
    justify-content: flex-start;
  }
  .cta-modern-business-hero .image-container {
    flex: 1;
    max-width: 45%;
  }
}
