css:
.cta-modern-minimal-purple {
  padding: 40px 20px;
  background-color: #fff;
  text-align: center;
}
.cta-modern-minimal-purple .container {
  max-width: 600px;
  margin: 0 auto;
}
.cta-modern-minimal-purple h2 {
  font-size: 28px;
  margin-bottom: 15px;
  color: #4b0082;
  font-family: 'Verdana', sans-serif;
}
.cta-modern-minimal-purple p {
  font-size: 16px;
  margin-bottom: 30px;
  color: #555;
  line-height: 1.6;
  font-family: 'Verdana', sans-serif;
}
.cta-modern-minimal-purple .btn {
  display: inline-block;
  padding: 12px 30px;
  background-color: #8a2be2;
  color: #fff;
  text-decoration: none;
  font-size: 16px;
  border-radius: 8px;
  transition: background-color 0.3s ease, transform 0.3s ease;
  font-family: 'Verdana', sans-serif;
}
.cta-modern-minimal-purple .btn:hover {
  background-color: #6a0dad;
  transform: translateY(-2px);
}

@media (min-width: 768px) {
  .cta-modern-minimal-purple {
    padding: 60px 20px;
  }
  .cta-modern-minimal-purple h2 {
    font-size: 36px;
  }
  .cta-modern-minimal-purple p {
    font-size: 18px;
  }
  .cta-modern-minimal-purple .btn {
    font-size: 18px;
  }
}
