css:
.cta-outlined-button-image {
    background-color: #fff;
    padding: 60px 0;
}
.cta-outlined-button-image .content-wrapper {
    display: flex;
    flex-wrap: wrap;
    align-items: center;
    max-width: 1100px;
    margin: 0 auto;
    padding: 0 20px;
}
.cta-outlined-button-image .text-content {
    flex: 1;
    padding-right: 40px;
    min-width: 300px;
}
.cta-outlined-button-image h2 {
    font-size: 2.8em;
    color: #333;
    margin-bottom: 15px;
    font-family: 'Montserrat', sans-serif;
}
.cta-outlined-button-image p {
    font-size: 1.1em;
    color: #555;
    line-height: 1.7;
    margin-bottom: 30px;
    font-family: 'Montserrat', sans-serif;
}
.cta-button-outline {
    display: inline-block;
    padding: 15px 35px;
    border: 2px solid #333;
    color: #333;
    text-decoration: none;
    font-size: 1.1em;
    font-weight: bold;
    border-radius: 30px;
    transition: all 0.3s ease;
    font-family: 'Montserrat', sans-serif;
}
.cta-button-outline:hover {
    background-color: #333;
    color: white;
}
.cta-outlined-button-image .image-container {
    flex: 1;
    min-width: 300px;
}
.cta-outlined-button-image img {
    display: block;
    width: 100%;
    height: auto;
    border-radius: 8px;
}
@media (max-width: 992px) {
    .cta-outlined-button-image .content-wrapper {
        flex-direction: column;
    }
    .cta-outlined-button-image .text-content {
        padding-right: 0;
        margin-bottom: 40px;
        text-align: center;
    }
    .cta-outlined-button-image .image-container {
        width: 80%;
        margin: 0 auto;
    }
}
@media (max-width: 768px) {
    .cta-outlined-button-image h2 {
        font-size: 2.2em;
    }
    .cta-outlined-button-image p {
        font-size: 1em;
    }
    .cta-button-outline {
        font-size: 1em;
        padding: 12px 30px;
    }
}
@media (max-width: 480px) {
    .cta-outlined-button-image {
        padding: 40px 15px;
    }
    .cta-outlined-button-image .image-container {
        width: 100%;
    }
    .cta-button-outline {
        width: 100%;
        box-sizing: border-box;
    }
}
