css:
.cta-space-age-orange-gradient {
  background: linear-gradient(120deg, #ffa500 0%, #ff8c00 100%); /* Bright Orange Gradient */
  color: #ffffff;
  padding: 75px 20px;
  font-family: 'Exo 2', sans-serif;
  text-align: center;
  position: relative;
  overflow: hidden;
}
.cta-space-age-orange-gradient::after {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: url('data:image/svg+xml;utf8,<svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 100 100" preserveAspectRatio="none"><path d="M0 0 L50 100 L100 0 Z" fill="rgba(255,255,255,0.1)"/></svg>');
  background-size: 50px 50px;
  opacity: 0.5;
}
.cta-space-age-orange-gradient .container {
  position: relative;
  z-index: 1;
  max-width: 700px;
  margin: 0 auto;
}
.cta-space-age-orange-gradient h2 {
  font-size: 2.9em;
  margin-bottom: 15px;
  font-weight: 700;
  letter-spacing: 1px;
  text-shadow: 1px 1px 4px rgba(0, 0, 0, 0.3);
}
.cta-space-age-orange-gradient p {
  font-size: 1.2em;
  line-height: 1.7;
  margin-bottom: 45px;
  font-weight: 300;
}
.cta-space-age-orange-gradient .cta-button {
  display: inline-block;
  padding: 18px 55px;
  background-color: #2c3e50; /* Dark Navy */
  color: #ffffff;
  text-decoration: none;
  font-weight: bold;
  border-radius: 40px; /* Very rounded */
  transition: background-color 0.3s ease, transform 0.3s ease;
  font-family: 'Exo 2', sans-serif;
  box-shadow: 0 6px 15px rgba(0, 0, 0, 0.25);
}
.cta-space-age-orange-gradient .cta-button:hover {
  background-color: #34495e;
  transform: translateY(-4px);
}

@media (min-width: 768px) {
  .cta-space-age-orange-gradient {
    padding: 120px 40px;
  }
  .cta-space-age-orange-gradient h2 {
    font-size: 3.8em;
  }
  .cta-space-age-orange-gradient p {
    font-size: 1.35em;
  }
}
