css:
.cta-split-layout-peach-green {
  display: flex;
  flex-direction: column;
  font-family: 'Roboto', sans-serif;
}
.cta-split-layout-peach-green .left-panel {
  background-color: #ffe5d9;
  padding: 40px 20px;
  color: #c06c84;
  text-align: center;
  flex: 1;
}
.cta-split-layout-peach-green .right-panel {
  background-color: #a8dadc;
  padding: 40px 20px;
  display: flex;
  justify-content: center;
  align-items: center;
  flex: 1;
}
.cta-split-layout-peach-green h2 {
  font-size: 28px;
  margin-bottom: 15px;
}
.cta-split-layout-peach-green p {
  font-size: 16px;
  line-height: 1.6;
}
.cta-split-layout-peach-green .btn {
  display: inline-block;
  padding: 12px 30px;
  background-color: #fff;
  color: #a8dadc;
  text-decoration: none;
  font-size: 16px;
  font-weight: bold;
  border-radius: 50px;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-split-layout-peach-green .btn:hover {
  background-color: #a8dadc;
  color: #fff;
}

@media (min-width: 768px) {
  .cta-split-layout-peach-green {
    flex-direction: row;
  }
  .cta-split-layout-peach-green .left-panel {
    text-align: left;
    padding: 60px 40px;
  }
  .cta-split-layout-peach-green .right-panel {
    padding: 60px 40px;
  }
  .cta-split-layout-peach-green h2 {
    font-size: 36px;
  }
  .cta-split-layout-peach-green p {
    font-size: 18px;
  }
  .cta-split-layout-peach-green .btn {
    font-size: 18px;
  }
}
