css:
.cta-split-screen-image-text {
    display: flex;
    min-height: 60vh;
}
.cta-split-screen-image-text .split-pane {
    flex: 1;
    display: flex;
    justify-content: center;
    align-items: center;
    padding: 40px 30px;
    box-sizing: border-box;
}
.cta-split-screen-image-text .image-pane {
    background-color: #f1f3f6;
    position: relative;
}
.cta-split-screen-image-text .image-pane img {
    display: block;
    max-width: 100%;
    height: auto;
    border-radius: 10px;
    object-fit: cover;
}
.cta-split-screen-image-text .text-pane {
    background-color: #ffffff;
}
.cta-split-screen-image-text .content {
    max-width: 450px;
    text-align: left;
}
.cta-split-screen-image-text h2 {
    font-size: 2.8em;
    color: #2c3e50;
    margin-bottom: 15px;
    font-family: 'Nunito', sans-serif;
}
.cta-split-screen-image-text p {
    font-size: 1.1em;
    color: #7f8c8d;
    line-height: 1.7;
    margin-bottom: 30px;
    font-family: 'Nunito', sans-serif;
}
.cta-button-accent {
    display: inline-block;
    padding: 15px 35px;
    background-color: #e67e22;
    color: white;
    text-decoration: none;
    font-size: 1.1em;
    font-weight: bold;
    border-radius: 30px;
    transition: background-color 0.3s ease;
    font-family: 'Nunito', sans-serif;
}
.cta-button-accent:hover {
    background-color: #d35400;
}
@media (max-width: 992px) {
    .cta-split-screen-image-text {
        flex-direction: column;
        min-height: auto;
    }
    .cta-split-screen-image-text .split-pane {
        flex-basis: 100%;
        padding: 50px 20px;
    }
    .cta-split-screen-image-text .image-pane {
        background-color: #f1f3f6;
    }
    .cta-split-screen-image-text .text-pane {
        background-color: #ffffff;
    }
    .cta-split-screen-image-text .content {
        text-align: center;
    }
}
@media (max-width: 768px) {
    .cta-split-screen-image-text h2 {
        font-size: 2.4em;
    }
    .cta-split-screen-image-text p {
        font-size: 1em;
    }
}
@media (max-width: 480px) {
    .cta-button-accent {
        width: 100%;
        box-sizing: border-box;
    }
}
