css:
.cta-testimonial-highlight {
  background-color: #f4f7f6;
  padding: 40px 20px;
  text-align: center;
  border-top: 2px solid #4CAF50;
}
.cta-testimonial-highlight .container {
  max-width: 700px;
  margin: 0 auto;
}
.cta-testimonial-highlight .testimonial {
  margin-bottom: 30px;
  font-style: italic;
  color: #555;
  position: relative;
  padding: 20px;
  background-color: #ffffff;
  border-left: 4px solid #4CAF50;
  box-shadow: 0 2px 5px rgba(0,0,0,0.1);
}
.cta-testimonial-highlight .testimonial p {
  font-size: 1.2em;
  line-height: 1.6;
  margin-bottom: 15px;
}
.cta-testimonial-highlight .testimonial footer {
  font-weight: bold;
  color: #333;
  font-style: normal;
}
.cta-testimonial-highlight .cta-learn-more {
  display: inline-block;
  padding: 12px 30px;
  background-color: #4CAF50;
  color: #ffffff;
  text-decoration: none;
  font-weight: bold;
  border-radius: 5px;
  transition: background-color 0.3s ease;
}
.cta-testimonial-highlight .cta-learn-more:hover {
  background-color: #45a049;
}
@media (min-width: 768px) {
  .cta-testimonial-highlight {
    padding: 60px 40px;
  }
  .cta-testimonial-highlight .testimonial p {
    font-size: 1.4em;
  }
}
