css:
.cta-section-1 {
  background-color: #000000;
  color: #FFFFFF;
  padding: 60px 20px;
  text-align: center;
}
.cta-container {
  max-width: 700px;
  margin: 0 auto;
}
.cta-section-1 h2 {
  font-size: 2.5rem;
  margin-bottom: 15px;
  font-weight: 900;
  line-height: 1.2;
}
.cta-section-1 p {
  font-size: 1.1rem;
  margin-bottom: 30px;
  color: #CCCCCC;
}
.cta-button {
  display: inline-block;
  padding: 15px 30px;
  background-color: #FF00FF;
  color: #FFFFFF;
  text-decoration: none;
  font-weight: 900;
  font-size: 1.2rem;
  border: 3px solid #FF00FF;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-button:hover {
  background-color: #FFFFFF;
  color: #FF00FF;
}

@media (min-width: 768px) {
  .cta-section-1 {
    padding: 80px 40px;
  }
  .cta-section-1 h2 {
    font-size: 3.5rem;
  }
  .cta-section-1 p {
    font-size: 1.3rem;
  }
  .cta-button {
    font-size: 1.4rem;
  }
}
