css:
.cta-section-2 {
  background-color: #FFFFFF;
  color: #000000;
  padding: 60px 20px;
  text-align: center;
}
.cta-section-2 .cta-container {
  max-width: 700px;
  margin: 0 auto;
}
.cta-section-2 .section-title {
  font-size: 2.8rem;
  margin-bottom: 15px;
  font-weight: 900;
  line-height: 1.2;
}
.cta-section-2 .section-text {
  font-size: 1.1rem;
  margin-bottom: 30px;
  color: #333333;
}
.cta-button-alt {
  display: inline-block;
  padding: 15px 30px;
  background-color: #00FFFF;
  color: #000000;
  text-decoration: none;
  font-weight: 900;
  font-size: 1.2rem;
  border: 3px solid #00FFFF;
  transition: background-color 0.3s ease, color 0.3s ease;
  cursor: pointer;
}
.cta-button-alt:hover {
  background-color: #000000;
  color: #00FFFF;
}

@media (min-width: 768px) {
  .cta-section-2 {
    padding: 80px 40px;
  }
  .cta-section-2 .section-title {
    font-size: 3.8rem;
  }
  .cta-section-2 .section-text {
    font-size: 1.3rem;
  }
  .cta-button-alt {
    font-size: 1.4rem;
  }
}
