css:
.cta-section-3 {
  background-color: #FF00FF;
  color: #FFFFFF;
  padding: 60px 20px;
  text-align: center;
  position: relative;
}
.cta-section-3::before {
  content: '';
  position: absolute;
  top: 0;
  left: 0;
  width: 100%;
  height: 100%;
  background: linear-gradient(135deg, #FF00FF 0%, #00FFFF 100%);
  opacity: 0.1;
  z-index: 0;
}
.cta-content {
  position: relative;
  z-index: 1;
  max-width: 800px;
  margin: 0 auto;
}
.cta-heading {
  font-size: 2.6rem;
  margin-bottom: 15px;
  font-weight: 900;
  line-height: 1.2;
}
.cta-subheading {
  font-size: 1.15rem;
  margin-bottom: 30px;
}
.cta-actions {
  display: flex;
  flex-direction: column;
  gap: 15px;
  align-items: center;
}
.cta-link-primary, .cta-link-secondary {
  display: inline-block;
  padding: 15px 30px;
  text-decoration: none;
  font-weight: 900;
  font-size: 1.15rem;
  border-radius: 0;
  transition: all 0.3s ease;
}
.cta-link-primary {
  background-color: #FFFFFF;
  color: #FF00FF;
  border: 3px solid #FFFFFF;
}
.cta-link-primary:hover {
  background-color: #FF00FF;
  color: #FFFFFF;
}
.cta-link-secondary {
  background-color: transparent;
  color: #FFFFFF;
  border: 3px solid #FFFFFF;
}
.cta-link-secondary:hover {
  background-color: #FFFFFF;
  color: #FF00FF;
}

@media (min-width: 768px) {
  .cta-section-3 {
    padding: 100px 40px;
  }
  .cta-heading {
    font-size: 4rem;
  }
  .cta-subheading {
    font-size: 1.4rem;
  }
  .cta-actions {
    flex-direction: row;
    justify-content: center;
  }
  .cta-link-primary, .cta-link-secondary {
    font-size: 1.3rem;
  }
}
