css:
.cta-section-4 {
  background-color: #000000;
  color: #FFFFFF;
  padding: 60px 20px;
  text-align: center;
  font-family: 'Arial Black', sans-serif;
}
.cta-wrapper {
  max-width: 750px;
  margin: 0 auto;
  border: 4px solid #FFFFFF;
  padding: 40px 30px;
}
.main-headline {
  font-size: 2.4rem;
  margin-bottom: 15px;
  font-weight: 900;
  line-height: 1.2;
  text-transform: uppercase;
}
.sub-headline {
  font-size: 1.1rem;
  margin-bottom: 30px;
  color: #DDDDDD;
}
.cta-action-button {
  display: inline-block;
  padding: 18px 35px;
  background-color: #FFFFFF;
  color: #000000;
  text-decoration: none;
  font-weight: 900;
  font-size: 1.3rem;
  border: 4px solid #FFFFFF;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-action-button:hover {
  background-color: #000000;
  color: #FFFFFF;
}

@media (min-width: 768px) {
  .cta-section-4 {
    padding: 100px 40px;
  }
  .cta-wrapper {
    padding: 60px 40px;
  }
  .main-headline {
    font-size: 4rem;
  }
  .sub-headline {
    font-size: 1.3rem;
  }
  .cta-action-button {
    font-size: 1.5rem;
  }
}
