css:
.cta-section-5 {
  background-color: #FFFFFF;
  color: #000000;
  padding: 60px 20px;
  text-align: center;
}
.cta-content-block {
  max-width: 700px;
  margin: 0 auto;
  border-left: 5px solid #FF00FF;
  padding-left: 30px;
  text-align: left;
}
.title {
  font-size: 2.5rem;
  margin-bottom: 15px;
  font-weight: 900;
  line-height: 1.2;
}
.subtitle {
  font-size: 1.1rem;
  margin-bottom: 30px;
  color: #444444;
}
.cta-btn-outline {
  display: inline-block;
  padding: 15px 30px;
  background-color: transparent;
  color: #FF00FF;
  text-decoration: none;
  font-weight: 900;
  font-size: 1.2rem;
  border: 3px solid #FF00FF;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-btn-outline:hover {
  background-color: #FF00FF;
  color: #FFFFFF;
}

@media (min-width: 768px) {
  .cta-section-5 {
    padding: 80px 40px;
  }
  .cta-content-block {
    padding-left: 50px;
    border-left-width: 8px;
  }
  .title {
    font-size: 3.5rem;
  }
  .subtitle {
    font-size: 1.3rem;
  }
  .cta-btn-outline {
    font-size: 1.4rem;
  }
}
