css:
.cta-section-8 {
  background-color: #FFFFFF;
  color: #000000;
  padding: 60px 20px;
  text-align: center;
  border-bottom: 4px solid #00FFFF;
}
.cta-content-high-contrast {
  max-width: 700px;
  margin: 0 auto;
}
.cta-title-hc {
  font-size: 2.6rem;
  margin-bottom: 15px;
  font-weight: 900;
  line-height: 1.2;
}
.cta-text-hc {
  font-size: 1.1rem;
  margin-bottom: 30px;
  color: #333333;
}
.cta-btn-hc {
  display: inline-block;
  padding: 15px 30px;
  background-color: #00FFFF;
  color: #000000;
  text-decoration: none;
  font-weight: 900;
  font-size: 1.2rem;
  border: 4px solid #00FFFF;
  transition: background-color 0.3s ease, color 0.3s ease;
}
.cta-btn-hc:hover {
  background-color: #000000;
  color: #00FFFF;
  border-color: #000000;
}

@media (min-width: 768px) {
  .cta-section-8 {
    padding: 80px 40px;
  }
  .cta-title-hc {
    font-size: 3.6rem;
  }
  .cta-text-hc {
    font-size: 1.3rem;
  }
  .cta-btn-hc {
    font-size: 1.4rem;
  }
}
