css:
.cta-section-blue-alert {
            padding: 80px 20px;
            background-color: #ffffff;
            color: #000000;
            text-align: center;
        }
        .cta-section-blue-alert .cta-content {
            max-width: 700px;
            margin: 0 auto;
        }
        .cta-section-blue-alert h2 {
            font-size: 3.5rem;
            margin-bottom: 20px;
            line-height: 1.1;
            font-weight: 900;
            letter-spacing: -1px;
        }
        .cta-section-blue-alert p {
            font-size: 1.1rem;
            margin-bottom: 40px;
            line-height: 1.6;
            color: #555555;
        }
        .cta-section-blue-alert .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: #007bff;
            color: #ffffff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #000000;
            transition: all 0.3s ease;
            text-transform: uppercase;
            box-shadow: 5px 5px 0px #000000;
        }
        .cta-section-blue-alert .cta-button:hover {
            background-color: #000000;
            color: #007bff;
            border-color: #007bff;
            box-shadow: 5px 5px 0px #007bff;
        }
        @media (max-width: 768px) {
            .cta-section-blue-alert h2 {
                font-size: 3rem;
            }
            .cta-section-blue-alert p {
                font-size: 1.1rem;
            }
            .cta-section-blue-alert .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
            }
        }
        @media (max-width: 480px) {
            .cta-section-blue-alert {
                padding: 60px 15px;
            }
            .cta-section-blue-alert h2 {
                font-size: 2.5rem;
                letter-spacing: -0.5px;
            }
            .cta-section-blue-alert p {
                font-size: 1rem;
            }
            .cta-section-blue-alert .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
                box-shadow: 3px 3px 0px #000000;
            }
            .cta-section-blue-alert .cta-button:hover {
                box-shadow: 3px 3px 0px #007bff;
            }
        }
