css:
.cta-section-blue-block {
            padding: 100px 20px;
            background-color: #007bff;
            color: #ffffff;
            text-align: center;
        }
        .cta-section-blue-block .cta-content {
            max-width: 750px;
            margin: 0 auto;
        }
        .cta-section-blue-block h2 {
            font-size: 3.3rem;
            margin-bottom: 20px;
            line-height: 1.15;
            font-weight: 900;
            letter-spacing: -1px;
        }
        .cta-section-blue-block p {
            font-size: 1.15rem;
            margin-bottom: 40px;
            line-height: 1.6;
        }
        .cta-section-blue-block .cta-button {
            display: inline-block;
            padding: 18px 50px;
            background-color: #ffffff;
            color: #007bff;
            text-decoration: none;
            font-size: 1.3rem;
            font-weight: 900;
            border: 4px solid #000000;
            transition: all 0.3s ease;
            text-transform: uppercase;
            box-shadow: 6px 6px 0px #000000;
        }
        .cta-section-blue-block .cta-button:hover {
            background-color: #000000;
            color: #ffffff;
            border-color: #ffffff;
            box-shadow: 6px 6px 0px #ffffff;
        }
        @media (max-width: 768px) {
            .cta-section-blue-block {
                padding: 80px 15px;
            }
            .cta-section-blue-block h2 {
                font-size: 3rem;
            }
            .cta-section-blue-block p {
                font-size: 1.1rem;
            }
            .cta-section-blue-block .cta-button {
                padding: 15px 40px;
                font-size: 1.2rem;
                box-shadow: 5px 5px 0px #000000;
            }
            .cta-section-blue-block .cta-button:hover {
                box-shadow: 5px 5px 0px #ffffff;
            }
        }
        @media (max-width: 480px) {
            .cta-section-blue-block {
                padding: 60px 10px;
            }
            .cta-section-blue-block h2 {
                font-size: 2.5rem;
                letter-spacing: -0.5px;
            }
            .cta-section-blue-block p {
                font-size: 1rem;
            }
            .cta-section-blue-block .cta-button {
                padding: 12px 35px;
                font-size: 1.1rem;
                box-shadow: 4px 4px 0px #000000;
            }
            .cta-section-blue-block .cta-button:hover {
                box-shadow: 4px 4px 0px #ffffff;
            }
        }
