css:
.cta-section-blue-overlay-button {
            padding: 100px 20px;
            background-color: #000000;
            color: #ffffff;
            text-align: center;
            position: relative;
            overflow: hidden;
        }
        .cta-section-blue-overlay-button::before {
            content: '';
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            background: linear-gradient(rgba(0, 123, 255, 0.1), rgba(0, 123, 255, 0.3));
            z-index: 1;
        }
        .cta-section-blue-overlay-button .cta-content {
            max-width: 700px;
            margin: 0 auto;
            position: relative;
            z-index: 2;
        }
        .cta-section-blue-overlay-button h2 {
            font-size: 3.2rem;
            margin-bottom: 20px;
            line-height: 1.2;
            font-weight: 900;
        }
        .cta-section-blue-overlay-button p {
            font-size: 1.1rem;
            margin-bottom: 40px;
            line-height: 1.6;
            color: #cccccc;
        }
        .cta-section-blue-overlay-button .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: #007bff;
            color: #ffffff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #ffffff;
            transition: all 0.3s ease;
            text-transform: uppercase;
            box-shadow: 0 0 0 3px #007bff;
        }
        .cta-section-blue-overlay-button .cta-button:hover {
            background-color: #ffffff;
            color: #007bff;
            border-color: #007bff;
            box-shadow: 0 0 0 8px #007bff;
        }
        @media (max-width: 768px) {
            .cta-section-blue-overlay-button {
                padding: 80px 15px;
            }
            .cta-section-blue-overlay-button h2 {
                font-size: 2.8rem;
            }
            .cta-section-blue-overlay-button p {
                font-size: 1.1rem;
            }
            .cta-section-blue-overlay-button .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
            }
        }
        @media (max-width: 480px) {
            .cta-section-blue-overlay-button {
                padding: 60px 10px;
            }
            .cta-section-blue-overlay-button h2 {
                font-size: 2.2rem;
            }
            .cta-section-blue-overlay-button p {
                font-size: 1rem;
            }
            .cta-section-blue-overlay-button .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
            }
        }
