css:
.cta-section-blue-sharp-corners {
            padding: 90px 20px;
            background-color: #007bff;
            color: #ffffff;
            text-align: center;
            border-radius: 0;
        }
        .cta-section-blue-sharp-corners .cta-content {
            max-width: 700px;
            margin: 0 auto;
        }
        .cta-section-blue-sharp-corners h2 {
            font-size: 3.2rem;
            margin-bottom: 20px;
            line-height: 1.2;
            font-weight: 900;
        }
        .cta-section-blue-sharp-corners p {
            font-size: 1.1rem;
            margin-bottom: 40px;
            line-height: 1.6;
        }
        .cta-section-blue-sharp-corners .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: #ffffff;
            color: #007bff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #000000;
            transition: all 0.3s ease;
            text-transform: uppercase;
            box-shadow: 5px 5px 0px #000000;
        }
        .cta-section-blue-sharp-corners .cta-button:hover {
            background-color: #000000;
            color: #ffffff;
            border-color: #ffffff;
            box-shadow: 5px 5px 0px #ffffff;
        }
        @media (max-width: 768px) {
            .cta-section-blue-sharp-corners {
                padding: 70px 15px;
            }
            .cta-section-blue-sharp-corners h2 {
                font-size: 2.8rem;
            }
            .cta-section-blue-sharp-corners p {
                font-size: 1.1rem;
            }
            .cta-section-blue-sharp-corners .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
                box-shadow: 4px 4px 0px #000000;
            }
            .cta-section-blue-sharp-corners .cta-button:hover {
                box-shadow: 4px 4px 0px #ffffff;
            }
        }
        @media (max-width: 480px) {
            .cta-section-blue-sharp-corners {
                padding: 50px 10px;
            }
            .cta-section-blue-sharp-corners h2 {
                font-size: 2.3rem;
            }
            .cta-section-blue-sharp-corners p {
                font-size: 1rem;
            }
            .cta-section-blue-sharp-corners .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
                box-shadow: 3px 3px 0px #000000;
            }
            .cta-section-blue-sharp-corners .cta-button:hover {
                box-shadow: 3px 3px 0px #ffffff;
            }
        }
