css:
.cta-section-blue-underline-button {
            padding: 80px 20px;
            background-color: #000000;
            color: #ffffff;
            text-align: center;
        }
        .cta-section-blue-underline-button .cta-content {
            max-width: 700px;
            margin: 0 auto;
        }
        .cta-section-blue-underline-button h2 {
            font-size: 3.4rem;
            margin-bottom: 20px;
            line-height: 1.1;
            font-weight: 900;
            letter-spacing: -1px;
        }
        .cta-section-blue-underline-button p {
            font-size: 1.1rem;
            margin-bottom: 40px;
            line-height: 1.6;
            color: #cccccc;
        }
        .cta-section-blue-underline-button .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: transparent;
            color: #007bff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #007bff;
            transition: all 0.3s ease;
            text-transform: uppercase;
            position: relative;
        }
        .cta-section-blue-underline-button .cta-button::after {
            content: '';
            position: absolute;
            bottom: -5px;
            left: 0;
            width: 100%;
            height: 3px;
            background-color: #ffffff;
            transform: scaleX(0);
            transform-origin: bottom left;
            transition: transform 0.3s ease;
        }
        .cta-section-blue-underline-button .cta-button:hover {
            color: #ffffff;
            border-color: #ffffff;
        }
        .cta-section-blue-underline-button .cta-button:hover::after {
            transform: scaleX(1);
            background-color: #ffffff;
        }
        @media (max-width: 768px) {
            .cta-section-blue-underline-button h2 {
                font-size: 3rem;
            }
            .cta-section-blue-underline-button p {
                font-size: 1.1rem;
            }
            .cta-section-blue-underline-button .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
            }
        }
        @media (max-width: 480px) {
            .cta-section-blue-underline-button {
                padding: 60px 15px;
            }
            .cta-section-blue-underline-button h2 {
                font-size: 2.5rem;
                letter-spacing: -0.5px;
            }
            .cta-section-blue-underline-button p {
                font-size: 1rem;
            }
            .cta-section-blue-underline-button .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
            }
        }
