css:
.cta-section-framed-pink {
            padding: 100px 20px;
            background-color: #ffffff;
            color: #000000;
        }
        .cta-section-framed-pink .cta-wrapper {
            max-width: 900px;
            margin: 0 auto;
            border: 4px solid #000000;
            padding: 60px 40px;
            text-align: center;
            box-shadow: 10px 10px 0px #ff00ff;
        }
        .cta-section-framed-pink h2 {
            font-size: 3rem;
            margin-bottom: 20px;
            line-height: 1.2;
            font-weight: 900;
        }
        .cta-section-framed-pink p {
            font-size: 1.15rem;
            margin-bottom: 40px;
            line-height: 1.6;
            color: #333333;
        }
        .cta-section-framed-pink .cta-button {
            display: inline-block;
            padding: 15px 45px;
            background-color: #ff00ff;
            color: #ffffff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #000000;
            transition: all 0.3s ease;
            text-transform: uppercase;
        }
        .cta-section-framed-pink .cta-button:hover {
            background-color: #000000;
            color: #ff00ff;
            border-color: #ff00ff;
            box-shadow: 5px 5px 0px #ff00ff;
        }
        @media (max-width: 992px) {
            .cta-section-framed-pink .cta-wrapper {
                max-width: 80%;
            }
        }
        @media (max-width: 768px) {
            .cta-section-framed-pink {
                padding: 80px 15px;
            }
            .cta-section-framed-pink .cta-wrapper {
                padding: 50px 30px;
                box-shadow: 8px 8px 0px #ff00ff;
            }
            .cta-section-framed-pink h2 {
                font-size: 2.8rem;
            }
            .cta-section-framed-pink p {
                font-size: 1.1rem;
            }
            .cta-section-framed-pink .cta-button {
                padding: 12px 35px;
                font-size: 1.1rem;
            }
        }
        @media (max-width: 480px) {
            .cta-section-framed-pink {
                padding: 60px 10px;
            }
            .cta-section-framed-pink .cta-wrapper {
                padding: 40px 25px;
                border-width: 3px;
                box-shadow: 6px 6px 0px #ff00ff;
            }
            .cta-section-framed-pink h2 {
                font-size: 2.2rem;
            }
            .cta-section-pink-shock p {
                font-size: 1rem;
            }
            .cta-section-framed-pink .cta-button {
                padding: 10px 30px;
                font-size: 1rem;
            }
        }
