css:
.cta-section-pink-angled-button {
            padding: 90px 20px;
            background-color: #ffffff;
            color: #000000;
            text-align: center;
        }
        .cta-section-pink-angled-button .cta-content {
            max-width: 700px;
            margin: 0 auto;
        }
        .cta-section-pink-angled-button h2 {
            font-size: 3.1rem;
            margin-bottom: 20px;
            line-height: 1.2;
            font-weight: 900;
        }
        .cta-section-pink-angled-button p {
            font-size: 1.1rem;
            margin-bottom: 40px;
            line-height: 1.6;
            color: #333333;
        }
        .cta-section-pink-angled-button .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: #ff00ff;
            color: #ffffff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #000000;
            transition: all 0.3s ease;
            text-transform: uppercase;
            transform: skewX(-15deg);
            box-shadow: 5px 5px 0px #000000;
        }
        .cta-section-pink-angled-button .cta-button span {
            display: inline-block;
            transform: skewX(15deg);
        }
        .cta-section-pink-angled-button .cta-button:hover {
            background-color: #000000;
            color: #ff00ff;
            border-color: #ff00ff;
            box-shadow: 5px 5px 0px #ff00ff;
        }
        @media (max-width: 768px) {
            .cta-section-pink-angled-button {
                padding: 70px 15px;
            }
            .cta-section-pink-angled-button h2 {
                font-size: 2.8rem;
            }
            .cta-section-pink-angled-button p {
                font-size: 1.1rem;
            }
            .cta-section-pink-angled-button .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
                transform: skewX(-10deg);
                box-shadow: 4px 4px 0px #000000;
            }
            .cta-section-pink-angled-button .cta-button span {
                transform: skewX(10deg);
            }
            .cta-section-pink-angled-button .cta-button:hover {
                box-shadow: 4px 4px 0px #ff00ff;
            }
        }
        @media (max-width: 480px) {
            .cta-section-pink-angled-button {
                padding: 50px 10px;
            }
            .cta-section-pink-angled-button h2 {
                font-size: 2.3rem;
            }
            .cta-section-pink-angled-button p {
                font-size: 1rem;
            }
            .cta-section-pink-angled-button .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
                transform: skewX(-8deg);
                box-shadow: 3px 3px 0px #000000;
            }
            .cta-section-pink-angled-button .cta-button span {
                transform: skewX(8deg);
            }
            .cta-section-pink-angled-button .cta-button:hover {
                box-shadow: 3px 3px 0px #ff00ff;
            }
        }
