css:
.cta-section-pink-minimal-border {
            padding: 70px 20px;
            background-color: #ffffff;
            color: #000000;
            text-align: center;
            border: 3px solid #ff00ff;
        }
        .cta-section-pink-minimal-border .cta-content {
            max-width: 650px;
            margin: 0 auto;
        }
        .cta-section-pink-minimal-border h2 {
            font-size: 3rem;
            margin-bottom: 20px;
            line-height: 1.2;
            font-weight: 900;
        }
        .cta-section-pink-minimal-border p {
            font-size: 1.1rem;
            margin-bottom: 35px;
            line-height: 1.6;
            color: #555555;
        }
        .cta-section-pink-minimal-border .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: #000000;
            color: #ffffff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #ff00ff;
            transition: all 0.3s ease;
            text-transform: uppercase;
        }
        .cta-section-pink-minimal-border .cta-button:hover {
            background-color: #ff00ff;
            color: #ffffff;
            border-color: #000000;
        }
        @media (max-width: 768px) {
            .cta-section-pink-minimal-border {
                padding: 60px 15px;
                border-width: 3px;
            }
            .cta-section-pink-minimal-border h2 {
                font-size: 2.8rem;
            }
            .cta-section-pink-minimal-border p {
                font-size: 1.1rem;
            }
            .cta-section-pink-minimal-border .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
            }
        }
        @media (max-width: 480px) {
            .cta-section-pink-minimal-border {
                padding: 50px 10px;
                border-width: 2px;
            }
            .cta-section-pink-minimal-border h2 {
                font-size: 2.3rem;
            }
            .cta-section-pink-minimal-border p {
                font-size: 1rem;
            }
            .cta-section-pink-minimal-border .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
            }
        }
