css:
.cta-section-pink-shock {
            padding: 80px 20px;
            background-color: #000000;
            color: #ffffff;
            text-align: center;
        }
        .cta-section-pink-shock .cta-content {
            max-width: 700px;
            margin: 0 auto;
        }
        .cta-section-pink-shock h2 {
            font-size: 3rem;
            margin-bottom: 20px;
            line-height: 1.2;
            font-weight: 900;
        }
        .cta-section-pink-shock p {
            font-size: 1.2rem;
            margin-bottom: 40px;
            line-height: 1.6;
            color: #cccccc;
        }
        .cta-section-pink-shock .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: #ff00ff;
            color: #ffffff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #ff00ff;
            transition: all 0.3s ease;
            text-transform: uppercase;
        }
        .cta-section-pink-shock .cta-button:hover {
            background-color: #ffffff;
            color: #ff00ff;
            border-color: #ffffff;
        }
        @media (max-width: 768px) {
            .cta-section-pink-shock h2 {
                font-size: 2.5rem;
            }
            .cta-section-pink-shock p {
                font-size: 1.1rem;
            }
            .cta-section-pink-shock .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
            }
        }
        @media (max-width: 480px) {
            .cta-section-pink-shock {
                padding: 60px 15px;
            }
            .cta-section-pink-shock h2 {
                font-size: 2rem;
            }
            .cta-section-pink-shock p {
                font-size: 1rem;
            }
            .cta-section-pink-shock .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
            }
        }
