css:
.cta-section-sharp-pink-line {
            padding: 80px 20px;
            background-color: #ffffff;
            color: #000000;
            text-align: center;
            border-top: 5px solid #ff00ff;
            border-bottom: 5px solid #ff00ff;
        }
        .cta-section-sharp-pink-line .cta-content {
            max-width: 650px;
            margin: 0 auto;
        }
        .cta-section-sharp-pink-line h2 {
            font-size: 3rem;
            margin-bottom: 20px;
            line-height: 1.2;
            font-weight: 900;
        }
        .cta-section-sharp-pink-line p {
            font-size: 1.15rem;
            margin-bottom: 40px;
            line-height: 1.6;
            color: #444444;
        }
        .cta-section-sharp-pink-line .cta-button {
            display: inline-block;
            padding: 15px 40px;
            background-color: #000000;
            color: #ffffff;
            text-decoration: none;
            font-size: 1.2rem;
            font-weight: 900;
            border: 3px solid #000000;
            transition: all 0.3s ease;
            text-transform: uppercase;
        }
        .cta-section-sharp-pink-line .cta-button:hover {
            background-color: #ff00ff;
            color: #ffffff;
            border-color: #ff00ff;
        }
        @media (max-width: 768px) {
            .cta-section-sharp-pink-line {
                padding: 70px 15px;
                border-width: 4px;
            }
            .cta-section-sharp-pink-line h2 {
                font-size: 2.8rem;
            }
            .cta-section-sharp-pink-line p {
                font-size: 1.1rem;
            }
            .cta-section-sharp-pink-line .cta-button {
                padding: 12px 30px;
                font-size: 1.1rem;
            }
        }
        @media (max-width: 480px) {
            .cta-section-sharp-pink-line {
                padding: 50px 10px;
                border-width: 3px;
            }
            .cta-section-sharp-pink-line h2 {
                font-size: 2.2rem;
            }
            .cta-section-sharp-pink-line p {
                font-size: 1rem;
            }
            .cta-section-sharp-pink-line .cta-button {
                padding: 10px 25px;
                font-size: 1rem;
            }
        }
