css:
.cta-bubble-form {
    font-family: 'Orbitron', sans-serif;
    background-color: #f0e6e0; /* Dusty Rose */
    color: #4a4a4a;
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 40px 20px;
    text-align: center;
}

.cta-bubble-form .cta-container {
    background-color: rgba(255, 255, 255, 0.8);
    border-radius: 30px;
    padding: 30px;
    box-shadow: 0 10px 20px rgba(0, 0, 0, 0.1);
    margin-bottom: 30px;
}

.cta-bubble-form h2 {
    font-size: 2.5em;
    margin-bottom: 15px;
    color: #505050;
    font-weight: 700;
}

.cta-bubble-form p {
    font-size: 1.1em;
    margin-bottom: 30px;
    line-height: 1.6;
    color: #666;
}

.cta-form {
    display: flex;
    flex-direction: column;
    align-items: center;
    gap: 20px;
}

.form-field label {
    display: block;
    margin-bottom: 8px;
    font-size: 0.9em;
    color: #505050;
    font-weight: 500;
}

.form-field input[type="email"] {
    padding: 15px 20px;
    border: 2px solid #b0a0b0; /* Muted Pastel Pink */
    border-radius: 25px;
    font-size: 1em;
    width: 100%;
    max-width: 350px;
    box-sizing: border-box;
    background-color: #fff;
    transition: border-color 0.3s ease;
}

.form-field input[type="email"]:focus {
    outline: none;
    border-color: #807080;
}

.cta-bubble-form button {
    background-color: #d4af37; /* Mustard Yellow */
    color: #333;
    border: none;
    padding: 15px 40px;
    border-radius: 30px;
    font-size: 1.2em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease;
    box-shadow: 0 5px 15px rgba(212, 175, 55, 0.4);
}

.cta-bubble-form button:hover {
    background-color: #b0902a; /* Darker Mustard */
    transform: translateY(-2px);
}

.cta-image-container img {
    width: 100%;
    max-width: 600px;
    height: auto;
    border-radius: 20px;
    margin-top: 40px;
    box-shadow: 0 5px 15px rgba(0,0,0,0.1);
}

@media (min-width: 768px) {
    .cta-bubble-form {
        flex-direction: row;
        padding: 60px 40px;
        text-align: left;
        gap: 40px;
    }
    .cta-bubble-form .cta-container {
        flex: 1;
        margin-bottom: 0;
        max-width: 50%;
        padding: 40px;
    }
    .cta-bubble-form h2 {
        font-size: 3em;
    }
    .cta-bubble-form p {
        font-size: 1.2em;
    }
    .cta-form {
        flex-direction: row;
        justify-content: flex-start;
    }
    .form-field input[type="email"] {
        width: auto;
    }
    .cta-bubble-form .cta-image-container {
        flex: 1;
        max-width: 50%;
        margin-top: 0;
    }
}

@media (min-width: 1024px) {
    .cta-bubble-form {
        padding: 80px 60px;
    }
    .cta-bubble-form .cta-container {
        padding: 50px;
    }
    .cta-bubble-form h2 {
        font-size: 3.5em;
    }
}
