css:
.cta-chrome-headline {
    font-family: 'Audiowide', sans-serif;
    background-color: #e0d8c0; /* Pale Mustard */
    color: #333;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 40px 20px;
    text-align: center;
    overflow: hidden;
}

.cta-chrome-headline .cta-content {
    position: relative;
    z-index: 2;
    background: rgba(255, 255, 255, 0.7);
    padding: 30px;
    border-radius: 25px;
    margin-bottom: 30px;
    box-shadow: 0 8px 16px rgba(0,0,0,0.1);
}

.cta-chrome-headline h1 {
    font-size: 2.8em;
    margin-bottom: 20px;
    color: transparent;
    background-clip: text;
    background-image: linear-gradient(to right, #a090a0, #c0b0c0); /* Muted metallic gradient */
    -webkit-text-stroke: 1px #5a5a5a;
    font-weight: 700;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.1);
}

.cta-chrome-headline p {
    font-size: 1.1em;
    margin-bottom: 30px;
    line-height: 1.6;
    color: #555;
}

.cta-button {
    background-color: #b0c4de; /* Pale Teal */
    color: #333;
    border: none;
    padding: 15px 35px;
    border-radius: 30px;
    font-size: 1.2em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease, box-shadow 0.3s ease;
    box-shadow: 0 4px 10px rgba(176, 196, 222, 0.5);
}

.cta-button:hover {
    background-color: #a0b4cd; /* Darker Pale Teal */
    transform: translateY(-3px);
    box-shadow: 0 6px 15px rgba(176, 196, 222, 0.7);
}

.cta-graphic img {
    width: 100%;
    max-width: 400px;
    height: auto;
    display: block;
    margin: 0 auto;
    border-radius: 15px;
    box-shadow: 0 4px 8px rgba(0,0,0,0.1);
}

@media (min-width: 768px) {
    .cta-chrome-headline {
        flex-direction: row;
        justify-content: space-between;
        align-items: center;
        padding: 60px 40px;
        text-align: left;
    }
    .cta-chrome-headline .cta-content {
        flex: 1;
        max-width: 50%;
        padding: 40px;
        margin-bottom: 0;
    }
    .cta-chrome-headline h1 {
        font-size: 3.5em;
    }
    .cta-chrome-headline p {
        font-size: 1.2em;
    }
    .cta-graphic {
        flex: 1;
        max-width: 50%;
        text-align: center;
    }
    .cta-graphic img {
        margin: 0;
    }
}

@media (min-width: 1024px) {
    .cta-chrome-headline {
        padding: 80px 60px;
    }
    .cta-chrome-headline .cta-content {
        padding: 50px;
    }
    .cta-chrome-headline h1 {
        font-size: 4em;
    }
}
