css:
.cta-curved-banner {
    font-family: 'Press Start 2P', cursive;
    background-color: #f8e8d8; /* Pale Peach */
    color: #444;
    padding: 50px 20px;
    position: relative;
    text-align: center;
    overflow: hidden;
}

.cta-curved-banner::before {
    content: '';
    position: absolute;
    bottom: 0;
    left: 0;
    width: 100%;
    height: 70px;
    background-color: #f0e6e0; /* Dusty Rose */
    border-radius: 50% / 50%;
    transform: scale(1.5);
    z-index: 0;
}

.cta-curved-banner .cta-content {
    position: relative;
    z-index: 2;
    max-width: 700px;
    margin: 0 auto;
}

.cta-curved-banner h2 {
    font-size: 2.8em;
    margin-bottom: 15px;
    color: #6a5a5a;
    font-weight: 700;
}

.cta-curved-banner p {
    font-size: 1.1em;
    margin-bottom: 30px;
    line-height: 1.7;
    color: #555;
}

.cta-input-group {
    display: flex;
    flex-direction: column;
    gap: 15px;
    margin-top: 20px;
}

.cta-input-group input[type="email"] {
    padding: 15px 20px;
    border: 2px solid #c0b0a0; /* Muted Silver-like */
    border-radius: 30px;
    font-size: 1em;
    background-color: #fff;
    transition: border-color 0.3s ease;
}

.cta-input-group input[type="email"]:focus {
    outline: none;
    border-color: #807070;
}

.cta-input-group button {
    background-color: #87ceeb; /* Pale Teal */
    color: #333;
    border: none;
    padding: 15px 30px;
    border-radius: 30px;
    font-size: 1.1em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease;
    box-shadow: 0 4px 8px rgba(135, 206, 235, 0.4);
}

.cta-input-group button:hover {
    background-color: #77bceb; /* Darker Pale Teal */
    transform: translateY(-2px);
}

.cta-background-graphic img {
    position: absolute;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    object-fit: cover;
    opacity: 0.3;
    z-index: 1;
}

@media (min-width: 768px) {
    .cta-curved-banner {
        padding: 80px 40px;
    }
    .cta-curved-banner::before {
        height: 120px;
    }
    .cta-curved-banner h2 {
        font-size: 3.5em;
    }
    .cta-curved-banner p {
        font-size: 1.2em;
    }
    .cta-input-group {
        flex-direction: row;
        justify-content: center;
        max-width: 500px;
        margin: 30px auto 0;
    }
    .cta-input-group input[type="email"] {
        flex-grow: 1;
    }
}

@media (min-width: 1024px) {
    .cta-curved-banner {
        padding: 100px 60px;
    }
    .cta-curved-banner::before {
        height: 150px;
    }
    .cta-curved-banner h2 {
        font-size: 4em;
    }
}
