css:
.cta-curvy-contact {
    font-family: 'Press Start 2P', cursive;
    background-color: #f8e8d8; /* Pale Peach */
    color: #444;
    padding: 50px 20px;
    position: relative;
    text-align: center;
    overflow: hidden;
}

.cta-curvy-contact .cta-content {
    position: relative;
    z-index: 2;
    max-width: 700px;
    margin: 0 auto;
    background-color: rgba(255, 255, 255, 0.7);
    padding: 30px;
    border-radius: 30px;
    box-shadow: 0 10px 20px rgba(0,0,0,0.1);
}

.cta-curvy-contact h2 {
    font-size: 2.8em;
    margin-bottom: 15px;
    color: #6a5a5a;
    font-weight: 700;
}

.cta-curvy-contact p {
    font-size: 1.1em;
    margin-bottom: 30px;
    line-height: 1.7;
    color: #555;
}

.cta-form .form-row {
    display: flex;
    flex-direction: column;
    gap: 15px;
    margin-bottom: 15px;
}

.cta-form input[type="text"],
.cta-form input[type="email"],
.cta-form textarea {
    padding: 15px 20px;
    border: 2px solid #c0b0a0; /* Muted Silver-like */
    border-radius: 30px;
    font-size: 1em;
    background-color: #fff;
    transition: border-color 0.3s ease;
    width: 100%;
    box-sizing: border-box;
}

.cta-form textarea {
    min-height: 120px;
    resize: vertical;
}

.cta-form input:focus,
.cta-form textarea:focus {
    outline: none;
    border-color: #807070;
}

.cta-form button {
    background-color: #87ceeb; /* Pale Teal */
    color: #333;
    border: none;
    padding: 15px 30px;
    border-radius: 30px;
    font-size: 1.1em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease;
    box-shadow: 0 4px 8px rgba(135, 206, 235, 0.4);
}

.cta-form button:hover {
    background-color: #77bceb; /* Darker Pale Teal */
    transform: translateY(-2px);
}

.cta-background-shape {
    position: absolute;
    bottom: -50px;
    left: -50px;
    width: 250px;
    height: 250px;
    background-color: rgba(255, 200, 180, 0.4);
    border-radius: 70% 30% 50% 50% / 30% 70% 50% 70%;
    transform: rotate(30deg);
    z-index: 1;
}

@media (min-width: 768px) {
    .cta-curvy-contact {
        padding: 80px 40px;
    }
    .cta-curvy-contact .cta-content {
        padding: 40px;
    }
    .cta-curvy-contact h2 {
        font-size: 3.5em;
    }
    .cta-curvy-contact p {
        font-size: 1.2em;
    }
    .cta-form .form-row {
        flex-direction: row;
    }
    .cta-form input[type="text"],
    .cta-form input[type="email"] {
        flex: 1;
    }
    .cta-background-shape {
        width: 350px;
        height: 350px;
        bottom: -80px;
        left: -80px;
    }
}

@media (min-width: 1024px) {
    .cta-curvy-contact {
        padding: 100px 60px;
    }
    .cta-curvy-contact .cta-content {
        padding: 50px;
    }
    .cta-curvy-contact h2 {
        font-size: 4em;
    }
}
