css:
.cta-optimistic-banner {
    font-family: 'Audiowide', sans-serif;
    background-color: #e8dcd0; /* Warm Beige */
    color: #333;
    display: flex;
    flex-direction: column;
    align-items: center;
    padding: 50px 20px;
    text-align: center;
    position: relative;
    overflow: hidden;
}

.cta-text {
    position: relative;
    z-index: 2;
    background-color: rgba(255, 255, 255, 0.75);
    padding: 30px;
    border-radius: 25px;
    margin-bottom: 30px;
    box-shadow: 0 8px 16px rgba(0,0,0,0.1);
}

.cta-optimistic-banner h1 {
    font-size: 2.5em;
    margin-bottom: 15px;
    color: #5a4a4a;
    font-weight: 700;
}

.cta-optimistic-banner p {
    font-size: 1.1em;
    margin-bottom: 30px;
    line-height: 1.7;
    color: #555;
}

.cta-button {
    background-color: #b0d0e0; /* Dusty Blue */
    color: #333;
    border: none;
    padding: 16px 40px;
    border-radius: 35px;
    font-size: 1.2em;
    font-weight: 700;
    cursor: pointer;
    transition: background-color 0.3s ease, transform 0.2s ease, box-shadow 0.3s ease;
    box-shadow: 0 5px 12px rgba(176, 208, 224, 0.5);
}

.cta-button:hover {
    background-color: #90b0c0; /* Darker Dusty Blue */
    transform: translateY(-3px);
    box-shadow: 0 7px 15px rgba(176, 208, 224, 0.7);
}

.cta-image img {
    width: 100%;
    max-width: 500px;
    height: auto;
    border-radius: 20px;
    margin: 0 auto;
    display: block;
    box-shadow: 0 4px 10px rgba(0,0,0,0.1);
    z-index: 1;
    position: relative;
}

@media (min-width: 768px) {
    .cta-optimistic-banner {
        flex-direction: row;
        justify-content: space-between;
        align-items: center;
        padding: 80px 40px;
        text-align: left;
    }
    .cta-text {
        flex: 1;
        max-width: 50%;
        padding: 40px;
        margin-bottom: 0;
    }
    .cta-optimistic-banner h1 {
        font-size: 3.2em;
    }
    .cta-optimistic-banner p {
        font-size: 1.2em;
    }
    .cta-image {
        flex: 1;
        max-width: 45%;
        text-align: center;
    }
}

@media (min-width: 1024px) {
    .cta-optimistic-banner {
        padding: 100px 60px;
    }
    .cta-text {
        padding: 50px;
    }
    .cta-optimistic-banner h1 {
        font-size: 4em;
    }
}

js:
window.addEventListener('load', async () => {
    const images = document.querySelectorAll('img[src^="https://pixabay.com/api/"]');
    for (const img of images) {
        try {
            const url = img.src;
            const response = await fetch(url);
            if (!response.ok) throw new Error('Network response was not ok');
            const data = await response.json();
            if (data.hits && data.hits.length > 0) {
                img.src = data.hits[0].largeImageURL;
            } else {
                img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
            }
        } catch (error) {
            console.error('Failed to load image:', error);
            img.src = 'https://via.placeholder.com/1280x720?text=Image+Not+Found';
        }
    }
});
